/**
 * @fileoverview Archivo de configuraci√≥n para Next.js.
 * Permite personalizar el comportamiento del framework, como la gesti√≥n de im√°genes,
 * las redirecciones, las variables de entorno y las optimizaciones de compilaci√≥n.
 * En este caso, se configuran las reescrituras para el proxy de la API y se ignoran
 * errores de TypeScript y ESLint durante la compilaci√≥n.
 */
import type {NextConfig} from 'next';

const nextConfig: NextConfig = {
  /* config options here */
  typescript: {
    ignoreBuildErrors: true,
  },
  eslint: {
    ignoreDuringBuilds: true,
  },
  images: {
    remotePatterns: [
      {
        protocol: 'https',
        hostname: 'placehold.co',
        port: '',
        pathname: '/**',
      },
    ],
  },
  async rewrites() {
    return [
      {
        source: '/api/:path*',
        destination: 'http://127.0.0.1:5000/:path*',
      },
    ]
  },
};

export default nextConfig;

