'use server';
/**
 * @fileOverview Un agente de IA para el servicio de atenci√≥n al cliente.
 *
 * - customerService - Una funci√≥n que maneja las consultas de los clientes.
 * - CustomerServiceInput - El tipo de entrada para la funci√≥n.
 * - CustomerServiceOutput - El tipo de retorno para la funci√≥n.
 */

import {ai} from '@/ai/genkit';
import {z} from 'genkit';

const CustomerServiceInputSchema = z.string().describe('The user query to the customer service bot.');
export type CustomerServiceInput = z.infer<typeof CustomerServiceInputSchema>;

const CustomerServiceOutputSchema = z.string().describe('The response from the customer service bot.');
export type CustomerServiceOutput = z.infer<typeof CustomerServiceOutputSchema>;

export async function customerService(input: CustomerServiceInput): Promise<CustomerServiceOutput> {
  return customerServiceFlow(input);
}

const prompt = ai.definePrompt({
  name: 'customerServicePrompt',
  input: {schema: CustomerServiceInputSchema},
  output: {schema: CustomerServiceOutputSchema},
  prompt: `You are a friendly and helpful customer service assistant for Royal-Fernet, a luxury watch store.

Your goal is to assist users with their questions about products, orders, and shipping. Be concise and professional.

User question: {{{prompt}}}`, 
});

const customerServiceFlow = ai.defineFlow(
  {
    name: 'customerServiceFlow',
    inputSchema: CustomerServiceInputSchema,
    outputSchema: CustomerServiceOutputSchema,
  },
  async (input) => {
    const {output} = await prompt(input);
    return output!;
  }
);

src/ai/flows/invoice-generator-flow.ts:
'use server';
/**
 * @fileOverview Un generador de facturas mediante IA.
 *
 * - generateInvoice - Una funci√≥n que genera una factura en formato HTML.
 * - GenerateInvoiceInput - El tipo de entrada para la funci√≥n.
 * - GenerateInvoiceOutput - El tipo de retorno para la funci√≥n.
 */

import {ai} from '@/ai/genkit';
import {z} from 'genkit';

const GenerateInvoiceInputSchema = z.object({
  customerName: z.string().describe('El nombre del cliente.'),
  productName: z.string().describe('El nombre del producto comprado.'),
  quantity: z.number().describe('La cantidad de productos comprados.'),
  price: z.number().describe('El precio unitario del producto.'),
  invoiceNumber: z.string().describe('El n√∫mero de factura √∫nico.'),
  date: z.string().describe('La fecha de la factura (YYYY-MM-DD).'),
});
export type GenerateInvoiceInput = z.infer<typeof GenerateInvoiceInputSchema>;

const GenerateInvoiceOutputSchema = z.object({
    invoiceHtml: z.string().describe('La factura completa en formato HTML.'),
});
export type GenerateInvoiceOutput = z.infer<typeof GenerateInvoiceOutputSchema>;


export async function generateInvoice(input: GenerateInvoiceInput): Promise<GenerateInvoiceOutput> {
  return invoiceGeneratorFlow(input);
}

const prompt = ai.definePrompt({
  name: 'invoiceGeneratorPrompt',
  input: {schema: GenerateInvoiceInputSchema},
  output: {schema: GenerateInvoiceOutputSchema},
  prompt: `
    You are an expert invoice designer. Create a professional and clean HTML invoice based on the following details.
    The design should be modern, using inline CSS for styling. Do not include any javascript or external stylesheets.
    The total should be calculated as quantity * price. The currency is USD.

    Details:
    - Customer Name: {{{customerName}}}
    - Product: {{{productName}}}
    - Quantity: {{{quantity}}}
    - Unit Price: \${{{price}}}
    - Invoice Number: {{{invoiceNumber}}}
    - Date: {{{date}}}

    Generate the complete HTML for the invoice.
  `,
});

const invoiceGeneratorFlow = ai.defineFlow(
  {
    name: 'invoiceGeneratorFlow',
    inputSchema: GenerateInvoiceInputSchema,
    outputSchema: GenerateInvoiceOutputSchema,
  },
  async (input) => {
    const {output} = await prompt(input);
    return output!;
  }
);

