/**
 * @fileoverview P√°gina de detalle de un producto espec√≠fico.
 * Muestra informaci√≥n detallada de un producto, incluyendo im√°genes, nombre,
 * precio, descripci√≥n y un bot√≥n para a√±adirlo al carrito.
 * Se obtiene el ID del producto de la URL.
 */
'use client';

import { useEffect, useState } from 'react';
import Image from 'next/image';
import { notFound } from 'next/navigation';
import { useCart } from '@/hooks/use-cart';
import { fetchProductById } from '@/lib/data';
import type { Product } from '@/lib/definitions';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious } from '@/components/ui/carousel';
import { Skeleton } from '@/components/ui/skeleton';

export default function ProductDetailPage({ params }: { params: { id: string } }) {
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(true);
  const { addToCart } = useCart();

  useEffect(() => {
    const getProduct = async () => {
      const fetchedProduct = await fetchProductById(params.id);
      if (!fetchedProduct) {
        notFound();
      }
      setProduct(fetchedProduct);
      setLoading(false);
    };
    getProduct();
  }, [params.id]);

  if (loading) {
    return <ProductDetailSkeleton />;
  }

  if (!product) {
    return notFound();
  }

  const originalPrice = product.price;
  const discountedPrice = product.discount
    ? originalPrice - (originalPrice * product.discount) / 100
    : originalPrice;

  return (
    <div className="container mx-auto py-12 px-4">
      <div className="grid md:grid-cols-2 gap-8 lg:gap-16">
        <div className="w-full">
          <Carousel>
            <CarouselContent>
              {product.images.map((img, index) => (
                <CarouselItem key={index}>
                  <div className="aspect-square relative w-full rounded-lg overflow-hidden">
                    <Image
                      src={img}
                      alt={`${product.name} image ${index + 1}`}
                      data-ai-hint="watch detail"
                      fill
                      className="object-cover"
                    />
                  </div>
                </CarouselItem>
              ))}
            </CarouselContent>
            <CarouselPrevious className="left-4" />
            <CarouselNext className="right-4" />
          </Carousel>
        </div>
        <div>
          <h1 className="text-3xl lg:text-4xl font-bold font-headline mb-2">{product.name}</h1>
          <p className="text-lg text-muted-foreground mb-4">{product.category}</p>

          {product.discount && (
            <div className="mb-4">
              <Badge variant="destructive">{product.discount}% OFF</Badge>
            </div>
          )}

          <div className="flex items-baseline gap-4 mb-6">
            <p className="text-3xl font-bold text-primary">${discountedPrice.toFixed(2)}</p>
            {product.discount && (
              <p className="text-xl text-muted-foreground line-through">${originalPrice.toFixed(2)}</p>
            )}
          </div>

          <p className="text-foreground/80 leading-relaxed mb-8">{product.description}</p>

          <Button size="lg" className="w-full md:w-auto" onClick={() => addToCart(product)}>
            Add to Cart
          </Button>
        </div>
      </div>
    </div>
  );
}

