/**
 * @fileoverview Componente del carrito de compras.
 * Se muestra como un panel lateral (sheet) y permite a los usuarios ver
 * los productos que han agregado, modificar las cantidades, eliminarlos
 * y proceder al pago, que ahora redirige a WhatsApp con los detalles del pedido.
 */
"use client";

import { useState, useEffect } from 'react';
import Image from 'next/image';
import { ShoppingCart, X, Plus, Minus } from 'lucide-react';
import { Button } from './ui/button';
import {
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
  SheetTrigger,
  SheetFooter,
} from './ui/sheet';
import { useCart } from '@/hooks/use-cart';
import { Separator } from './ui/separator';

export function CartSheet() {
  const { cartItems, removeFromCart, updateQuantity, cartTotal, clearCart } = useCart();
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    setIsMounted(true);
  }, []);

  const handleCheckout = () => {
    // IMPORTANT: Replace with your actual WhatsApp phone number
    const phoneNumber = "1234567890"; 

    let message = "¬°Hola! Quisiera hacer un pedido de los siguientes art√≠culos:\n\n";

    cartItems.forEach(item => {
        const itemPrice = item.product.discount
            ? item.product.price - (item.product.price * item.product.discount / 100)
            : item.product.price;
        const totalItemPrice = itemPrice * item.quantity;

        message += `*${item.product.name}*\n`;
        message += `(Ref: ${item.product.id})\n`;
        message += `Cantidad: ${item.quantity}\n`;
        message += `Imagen: ${item.product.images[0]}\n`;
        message += `Subtotal: $${totalItemPrice.toFixed(2)}\n\n`;
    });

    message += `*Monto Total: $${cartTotal.toFixed(2)}*\n\n`;
    message += "Por favor, confirme mi pedido y proporcione los detalles de pago. ¬°Gracias!";

    // The image URL is now included in the message text.
    // Note: Including images directly in a WhatsApp message via URL is not reliably supported.
    // The product reference ID and name are included for clear identification.

    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;

    window.open(whatsappUrl, '_blank');
    clearCart();
  };

  const itemCount = cartItems.reduce((acc, item) => acc + item.quantity, 0);

  if (!isMounted) {
    return (
      <Button variant="ghost" size="icon" className="relative">
        <ShoppingCart className="h-5 w-5" />
        <span className="sr-only">Open Cart</span>
      </Button>
    )
  }

  return (
    <Sheet>
      <SheetTrigger asChild>
        <Button variant="ghost" size="icon" className="relative">
          <ShoppingCart className="h-5 w-5" />
          {itemCount > 0 && (
            <span className="absolute -top-1 -right-1 flex h-4 w-4 items-center justify-center rounded-full bg-primary text-xs text-primary-foreground">
              {itemCount}
            </span>
          )}
          <span className="sr-only">Open Cart</span>
        </Button>
      </SheetTrigger>
      <SheetContent className="flex w-full flex-col pr-0 sm:max-w-lg">
        <SheetHeader className="px-6">
          <SheetTitle>Carrito de Compras ({itemCount})</SheetTitle>
        </SheetHeader>
        <Separator />
        {cartItems.length > 0 ? (
          <>
            <div className="flex-1 overflow-y-auto px-6">
              <div className="flex flex-col gap-4 py-4">
                {cartItems.map(({ product, quantity }) => (
                  <div key={product.id} className="flex items-center space-x-4">
                    <Image
                      src={product.images[0]}
                      alt={product.name}
                      width={64}
                      height={64}
                      className="rounded-md object-cover"
                    />
                    <div className="flex-1">
                      <p className="font-medium">{product.name}</p>
                      <div className="flex items-center gap-2 mt-1">
                        <Button variant="outline" size="icon" className="h-6 w-6" onClick={() => updateQuantity(product.id, quantity - 1)}>
                            <Minus className="h-3 w-3" />
                        </Button>
                        <span className="w-4 text-center">{quantity}</span>
                         <Button variant="outline" size="icon" className="h-6 w-6" onClick={() => updateQuantity(product.id, quantity + 1)}>
                            <Plus className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                    <div className="flex flex-col items-end">
                      <p className="font-semibold">${(product.price * quantity).toFixed(2)}</p>
                      <Button variant="ghost" size="icon" className="h-8 w-8 text-muted-foreground" onClick={() => removeFromCart(product.id)}>
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <Separator />
            <SheetFooter className="px-6 py-4 bg-secondary">
                <div className="w-full space-y-4">
                    <div className="flex justify-between font-semibold text-lg">
                        <span>Total</span>
                        <span>${cartTotal.toFixed(2)}</span>
                    </div>
                    <Button className="w-full" size="lg" onClick={handleCheckout}>Pagar por WhatsApp</Button>
                </div>
            </SheetFooter>
          </>
        ) : (
          <div className="flex flex-1 items-center justify-center">
            <p className="text-muted-foreground">
              Tu carrito est√° vac√≠o.
            </p>
          </div>
        )}
      </SheetContent>
    </Sheet>
  );
}

src/components/customer-service-widget.t:
/**
 * @fileoverview Componente del widget de chat de servicio al cliente.
 * Muestra un bot√≥n flotante que abre un popover con una interfaz de chat.
 * Utiliza un flujo de Genkit para proporcionar respuestas generadas por IA a las
 * consultas de los usuarios en tiempo real.
 */
"use client";

import { useState } from 'react';
import { MessageSquare, Send, Bot, User } from 'lucide-react';
import { Button } from './ui/button';
import { Popover, PopoverContent, PopoverTrigger } from './ui/popover';
import { Input } from './ui/input';
import { ScrollArea } from './ui/scroll-area';
import { customerService } from '@/ai/flows/customer-service-flow';
import { Avatar, AvatarFallback, AvatarImage } from './ui/avatar';
import { cn } from '@/lib/utils';

type Message = {
    role: 'user' | 'assistant';
    content: string;
};

export function CustomerServiceWidget() {
    const [messages, setMessages] = useState<Message[]>([
        { role: 'assistant', content: "Hello! How can I help you today?" }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!input.trim() || loading) return;

        const userMessage: Message = { role: 'user', content: input };
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setLoading(true);

        try {
            const response = await customerService(input);
            const assistantMessage: Message = { role: 'assistant', content: response };
            setMessages(prev => [...prev, assistantMessage]);
        } catch (error) {
            console.error("Error calling customer service AI:", error);
            const errorMessage: Message = { role: 'assistant', content: "Sorry, I'm having trouble connecting. Please try again later." };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Popover>
            <PopoverTrigger asChild>
                <Button 
                    size="icon" 
                    className="fixed bottom-6 right-6 h-14 w-14 rounded-full shadow-lg"
                    aria-label="Open customer service chat"
                >
                    <MessageSquare className="h-7 w-7" />
                </Button>
            </PopoverTrigger>
            <PopoverContent className="w-80 h-96 flex flex-col p-0 mr-4" align="end">
                <div className="p-4 bg-primary text-primary-foreground rounded-t-md">
                    <h3 className="font-semibold">Customer Service</h3>
                    <p className="text-xs text-primary-foreground/80">Powered by AI</p>
                </div>
                <ScrollArea className="flex-1 p-4">
                    <div className="space-y-4">
                        {messages.map((message, index) => (
                            <div key={index} className={cn(
                                "flex items-start gap-2",
                                message.role === 'user' ? 'justify-end' : 'justify-start'
                            )}>
                                {message.role === 'assistant' && (
                                    <Avatar className="h-6 w-6">
                                        <AvatarFallback><Bot className="h-4 w-4" /></AvatarFallback>
                                    </Avatar>
                                )}
                                <div className={cn(
                                    "rounded-lg px-3 py-2 text-sm max-w-[80%]",
                                    message.role === 'user' ? 'bg-primary text-primary-foreground' : 'bg-muted'
                                )}>
                                    {message.content}
                                </div>
                                {message.role === 'user' && (
                                     <Avatar className="h-6 w-6">
                                        <AvatarFallback><User className="h-4 w-4" /></AvatarFallback>
                                    </Avatar>
                                )}
                            </div>
                        ))}
                         {loading && (
                            <div className="flex items-start gap-2 justify-start">
                                 <Avatar className="h-6 w-6">
                                    <AvatarFallback><Bot className="h-4 w-4" /></AvatarFallback>
                                </Avatar>
                                <div className="rounded-lg px-3 py-2 text-sm bg-muted">
                                    <div className="flex items-center space-x-1">
                                        <span className="h-2 w-2 bg-foreground rounded-full animate-bounce [animation-delay:-0.3s]"></span>
                                        <span className="h-2 w-2 bg-foreground rounded-full animate-bounce [animation-delay:-0.15s]"></span>
                                        <span className="h-2 w-2 bg-foreground rounded-full animate-bounce"></span>
                                    </div>
                                </div>
                            </div>
                         )}
                    </div>
                </ScrollArea>
                <form onSubmit={handleSubmit} className="p-4 border-t">
                    <div className="relative">
                        <Input 
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            placeholder="Ask a question..."
                            disabled={loading}
                        />
                        <Button type="submit" size="icon" variant="ghost" className="absolute top-0 right-0 h-full" disabled={loading}>
                            <Send className="h-4 w-4"/>
                        </Button>
                    </div>
                </form>
            </PopoverContent>
        </Popover>
    );
}

