 * Contiene el logotipo, los enlaces de navegaci√≥n a las secciones principales
 * del sitio, un buscador, el selector de tema, el acceso al perfil de usuario y al carrito de compras.
 */
"use client";

import Link from 'next/link';
import { useRouter, useSearchParams } from 'next/navigation';
import { Gem, User, Menu, Search } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Sheet, SheetContent, SheetTrigger } from '@/components/ui/sheet';
import { CartSheet } from './cart-sheet';
import { ThemeToggle } from './theme-toggle';
import { Input } from './ui/input';
import { useEffect, useState } from 'react';

const navLinks = [
  { href: '/', label: 'Home' },
  { href: '/catalog', label: 'Catalog' },
  { href: '/#contact', label: 'Contact' },
];

function SearchBar() {
    const router = useRouter();
    const searchParams = useSearchParams();
    const [query, setQuery] = useState(searchParams.get('q') || '');

    useEffect(() => {
        setQuery(searchParams.get('q') || '');
    }, [searchParams]);

    const handleSearch = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const formData = new FormData(event.currentTarget);
        const searchQuery = formData.get('search') as string;
        if (searchQuery.trim()) {
            router.push(`/catalog?q=${encodeURIComponent(searchQuery.trim())}`);
        } else {
            router.push('/catalog');
        }
    };

    return (
        <form onSubmit={handleSearch} className="relative w-full ml-auto">
            <Input
                type="search"
                name="search"
                placeholder="Search products..."
                className="pr-10"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            <Button type="submit" size="icon" variant="ghost" className="absolute top-0 right-0 h-full">
                <Search className="h-4 w-4" />
                <span className="sr-only">Search</span>
            </Button>
        </form>
    )
}

