/**
 * @fileoverview Server Actions para la aplicaci√≥n.
 * Este archivo centraliza las operaciones de backend que se pueden invocar directamente
 * desde los componentes de React. Se encarga de la l√≥gica de negocio como la autenticaci√≥n
 * de usuarios, la gesti√≥n de productos y la administraci√≥n de usuarios, interactuando con la API de Python.
 * Utiliza 'revalidatePath' para asegurar que los datos se actualicen en el cliente
 * despu√©s de una mutaci√≥n.
 */
'use server';

import { revalidatePath } from 'next/cache';
import { redirect } from 'next/navigation';
import { generateInvoice, GenerateInvoiceInput } from '@/ai/flows/invoice-generator-flow';
import { fetchProductById } from './data';

const API_BASE_URL = 'http://127.0.0.1:5000/api'; // Usa la URL absoluta en el servidor

export async function loginUser(formData: FormData) {
  const email = formData.get('email');
  const password = formData.get('password');

  // Esta es la "llave maestra" para el super admin.
  if (email === 'master@royalfernet.com' && password === 'supersecret') {
    console.log('Inicio de sesi√≥n de administrador maestro exitoso.');
    redirect('/admin/dashboard');
  }

  try {
    const res = await fetch(`${API_BASE_URL}/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });

    if (!res.ok) { 
        // Si las credenciales son inv√°lidas, redirige con un error.
        return redirect('/admin?error=InvalidCredentials'); 
    }
    const user = await res.json();
    // Aqu√≠ establecer√≠as una cookie de sesi√≥n o un token para el usuario logueado.
    // Ejemplo: cookies().set('session', user.token, { httpOnly: true, path: '/' });

    // Si el login es exitoso, redirige al dashboard.
    redirect('/admin/dashboard');
  } catch (error) {
    console.error("Error de conexi√≥n durante el login:", error);
    // Si hay un error de red (backend no disponible), redirige con un error.
    return redirect('/admin?error=ConnectionFailed');
  }
}

export async function registerUser(formData: FormData) {
  const name = formData.get('name');
  const email = formData.get('email');
  const password = formData.get('password');

  await fetch(`${API_BASE_URL}/register`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ name, email, password }),
  });

  redirect('/login');
}

export async function addProduct(formData: FormData) {
  const images = [
    formData.get('image1'),
    formData.get('image2'),
    formData.get('image3'),
    formData.get('image4'),
  ].filter(img => img && typeof img === 'string' && img.trim() !== '').map(img => (img as string).trim());

  const productData = {
    name: formData.get('name'),
    description: formData.get('description'),
    category: formData.get('category'),
    price: Number(formData.get('price')),
    discount: Number(formData.get('discount')) || undefined,
    images: images,
    isFeatured: formData.get('isFeatured') === 'on',
  };

  const res = await fetch(`${API_BASE_URL}/products`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(productData),
  });

  if (!res.ok) { 
    console.error("Fall√≥ al a√±adir el producto", await res.text());
    return { error: 'Fall√≥ al a√±adir el producto' }; 
  }

  revalidatePath('/admin/dashboard');
  revalidatePath('/catalog');
  revalidatePath('/');
  redirect('/admin/dashboard');
}

export async function updateProduct(id: string, formData: FormData) {
    const images = [
      formData.get('image1'),
      formData.get('image2'),
      formData.get('image3'),
      formData.get('image4'),
    ].filter(img => img && typeof img === 'string' && img.trim() !== '').map(img => (img as string).trim());

    const productData = {
        name: formData.get('name'),
        description: formData.get('description'),
        category: formData.get('category'),
        price: Number(formData.get('price')),
        discount: Number(formData.get('discount')) || undefined,
        images: images,
        isFeatured: formData.get('isFeatured') === 'on',
    };

    const res = await fetch(`${API_BASE_URL}/products/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(productData),
    });

    if (!res.ok) { 
        console.error("Fall√≥ al actualizar el producto", await res.text());
        return { error: 'Fall√≥ al actualizar el producto' }; 
    }

    revalidatePath('/admin/dashboard');
    revalidatePath(`/product/${id}`);
    revalidatePath('/catalog');
    revalidatePath('/');
    redirect('/admin/dashboard');
}

export async function deleteProduct(id: string) {
    const res = await fetch(`${API_BASE_URL}/products/${id}`, {
      method: 'DELETE',
    });
    if (!res.ok) { 
        console.error("Fall√≥ al eliminar el producto", await res.text());
        return { error: 'Fall√≥ al eliminar el producto' }; 
    }

    revalidatePath('/admin/dashboard');
    revalidatePath('/catalog');
    revalidatePath('/');
}

export async function addAdmin(formData: FormData) {
  const adminData = {
    name: formData.get('name'),
    email: formData.get('email'),
    password: formData.get('password'),
  };

  const res = await fetch(`${API_BASE_URL}/admins`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(adminData),
  });

  if (!res.ok) {
    console.error("Fall√≥ al a√±adir el administrador", await res.text());
    return { error: 'Fall√≥ al a√±adir el administrador' };
  }

  revalidatePath('/admin/dashboard');
  redirect('/admin/dashboard');
}

export async function updateStoreSettings(formData: FormData) {
  const settingsData = {
    title: formData.get('storeTitle'),
    address: formData.get('storeAddress'),
    hours: formData.get('storeHours'),
    mainImageUrl: formData.get('storeMainImage'),
    mapImageUrl: formData.get('storeMapImage'),
    phone: formData.get('phone'),
    contactEmail: formData.get('contactEmail'),
    twitterUrl: formData.get('twitterUrl'),
    instagramUrl: formData.get('instagramUrl'),
    facebookUrl: formData.get('facebookUrl'),
  };

  const res = await fetch(`${API_BASE_URL}/settings`, {
    method: 'POST', // Usamos POST para crear/actualizar
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(settingsData),
  });

  if (!res.ok) {
    console.error("Fall√≥ al actualizar la configuraci√≥n de la tienda", await res.text());
    return { error: 'Fall√≥ al actualizar la configuraci√≥n de la tienda' };
  }

  revalidatePath('/admin/dashboard');
  revalidatePath('/');
  revalidatePath('/components/footer');
  redirect('/admin/dashboard');
}

export async function generateInvoiceAction(formData: FormData) {
  const customerName = formData.get('customerName') as string;
  const productId = formData.get('productId') as string;
  const quantity = Number(formData.get('quantity'));

  if (!productId) {
    return { success: false, error: 'Por favor, selecciona un producto.' };
  }

  const product = await fetchProductById(productId);

  if (!product) {
    return { success: false, error: 'Producto no encontrado. Aseg√∫rate de que el backend est√© funcionando y que el producto exista.' };
  }

  const invoiceData: GenerateInvoiceInput = {
    customerName: customerName,
    productName: product.name,
    quantity: quantity,
    price: product.price,
    invoiceNumber: `INV-${Date.now()}`,
    date: new Date().toISOString().split('T')[0],
  };

  try {
    const result = await generateInvoice(invoiceData);
    return { success: true, html: result.invoiceHtml };
  } catch (error) {
    console.error("Error generating invoice:", error);
    return { success: false, error: 'No se pudo generar la factura. Revisa la configuraci√≥n de Genkit/servicio de IA.' };
  }
}

export async function sendNotificationAction(formData: FormData): Promise<{success: boolean, error?: string}> {
  const message = formData.get('message') as string;
  const imageUrl = formData.get('imageUrl') as string;
  const linkUrl = formData.get('linkUrl') as string;

  if (!message) {
      return { success: false, error: "El mensaje es obligatorio." };
  }

  const notificationData = {
      message,
      imageUrl: imageUrl || undefined,
      linkUrl: linkUrl || undefined,
  };

  try {
    const res = await fetch(`${API_BASE_URL}/notifications`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(notificationData),
    });

    if (!res.ok) {
      const errorText = await res.text();
      console.error("Error al enviar la notificaci√≥n:", errorText);
      return { success: false, error: `No se pudo enviar la notificaci√≥n: ${errorText}` };
    }

    console.log('Notificaci√≥n enviada:', notificationData);
    revalidatePath('/'); // Se revalida para que la notificaci√≥n aparezca en la p√°gina principal.
    return { success: true };

  } catch (error) {
    console.error("Error de red al enviar la notificaci√≥n:", error);
    return { success: false, error: 'Ocurri√≥ un error de red al enviar la notificaci√≥n.' };
  }
}

