/**
 * @fileoverview Funciones de utilidad.
 * Contiene la funci√≥n `cn`, que combina y fusiona clases de Tailwind CSS
 * de forma segura, evitando conflictos y facilitando la creaci√≥n de
 * componentes con variantes de estilo condicionales.
 */
import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

